mapper.delete:

	delete 
	from board
	where no=#{no} and member_no=#{memberNo}

mapper.update:

	update 
	board
	set title=#{title}, content=#{content}
	where no=#{no}


	update 
	board 
	set view_cnt = view_cnt + 1 where no=#{no}

		
		update board
		   set order_no = order_no + 1		
         where order_no > #{parentOrderNo }
